+"		t = time.NewTimer(h.dt)\n"
+"		timeout = t.C\n"
+"	}\n"
+"	done := make(chan struct{})\n"
+"	tw := &timeoutWriter{\n"
+"		w: w,\n"
+"		h: make(Header),\n"
+"	}\n"
+"	go func() {\n"
+"		h.handler.ServeHTTP(tw, r)\n"
+"		close(done)\n"
+"	}()\n"
+"	select {\n"
+"	case <-done:\n"
+"		tw.mu.Lock()\n"
+"		defer tw.mu.Unlock()\n"
+"		dst := w.Header()\n"
+"		for k, vv := range tw.h {\n"
+"			dst[k] = vv\n"
+"		}\n"
+"		if !tw.wroteHeader {\n"
+"			tw.code = StatusOK\n"
+"		}\n"
+"		w.WriteHeader(tw.code)\n"
+"		w.Write(tw.wbuf.Bytes())\n"
+"		if t != nil {\n"
+"			t.Stop()\n"
+"		}\n"
+"	case <-timeout:\n"
+"		tw.mu.Lock()\n"
+"		defer tw.mu.Unlock()\n"
+"		w.WriteHeader(StatusServiceUnavailable)\n"
+"		io.WriteString(w, h.errorBody())\n"
+"		tw.timedOut = true\n"
